import { useState } from "react";
import { Modal, Button } from "react-bootstrap";
import "./profile.css";
import Card from "../../components/Card/Card";
import useAuth from "../../context&hook/useAuth.jsx";


const Profile = () => {
    const { user } = useAuth(); 
    const [showModal, setShowModal] = useState(false);

    const cardData = (
        <>
            {user.userEmail}
            <br />
            {user.userRole}
            <br />
            <br />
            <i>{"Click to see more details"}</i>
        </>
    );

    const handleOpenModal = () => {
        setShowModal(true);
    };

    const handleCloseModal = () => {
        setShowModal(false);
    };

    return (    
        <div className="profile-main">   
            <img src={require("../../assets/user-logo.png")} alt="user-logo" id="user"/>
            <Card title={user.userName} description={cardData}/>

            <Modal show={showModal} onHide={handleCloseModal} centered>
                <Modal.Header closeButton>
                    <Modal.Title>{user.userName}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <p><strong>Email:</strong> {user.userEmail}</p>
                    <p><strong>Role:</strong> {user.userRole}</p>
                    <p>More details can be placed here...</p>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleCloseModal}>
                        Close
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
};

export default Profile;
