import React from "react";
import Card from "./../../components/Card/Card.jsx";
import "./About.css";
import { Container, Row, Col, Image, Button } from "react-bootstrap";
const About = () => {
    return (
        <div className="d-flex flex-column min-vh-100">
        <Container>
          <Row className="align-items-center">
            <Col md={6}>
              <Image src="../../assets/about.jpg" fluid />
            </Col>
            <Col md={6}>
              <h2>Who We Are</h2>
              <p>Encoded is an ed-tech company specializing in technology and programming. We are passionate about making learning fun and accessible for everyone. Our mission is to empower people to learn new skills and achieve their full potential.</p>
              <Button variant="primary">Learn More</Button>
            </Col>
          </Row>
          {/* ... other sections ... */}
        </Container>
      </div>
    );
};

export default About;
