import React from "react";
import "./About.css";
import { Container, Row, Col, Image} from "react-bootstrap";
const About = () => {
    return (
        <div className="custom-bg d-flex flex-column min-vh-100 p-3">
        <Container>
          <Row className="align-items-center m-5">
            <Col md={6}>
              <Image src= {require("../../assets/about-1.jpeg")}  fluid />
            </Col>
            <Col md={6} className="p-6">
              <h1>Who We Are</h1>
              <p>Encoded is an ed-tech company specializing in technology and programming. We are passionate about making learning fun and accessible for everyone. Our mission is to empower people to learn new skills and achieve their full potential.</p>
            </Col>
          </Row>
          <Row className="align-items-center m-5">
            
            <Col md={6}>
              <h1>Who We Are</h1>
              <p>Encoded is an ed-tech company specializing in technology and programming. We are passionate about making learning fun and accessible for everyone. Our mission is to empower people to learn new skills and achieve their full potential.</p>
            </Col>
            <Col md={6}>
              <Image src= {require("../../assets/about-2.jpeg")}  fluid />
            </Col>
          </Row>
          {/* ... other sections ... */}
        </Container>
      </div>
    );
};

export default About;
