import React from "react";
import "./About.css";
import { Container, Row, Col, Image} from "react-bootstrap";
const About = () => {
    return (
        <div className="custom-bg d-flex min-vh-100 p-3">
        <Container>
          <Row className="align-items-center m-5 ">
            <Col md={6}>
              <Image src= {require("../../assets/about-1.jpeg")}  fluid />
            </Col>
            <Col md={6} className="brdr" style={{borderRight: "3px solid #d56b50"}}>
              <h1 className="subtitle">Who We Are</h1>
              <p className="subtext">Encoded is an ed-tech company specializing in technology and programming. We are passionate about making learning fun and accessible for everyone. Our mission is to empower people to learn new skills and achieve their full potential.</p>
            </Col>
          </Row>
          <Row className="align-items-center m-5">
            <video src="../../assets/bg-vid.mp4" controls className="col-md-6"></video>
</Row>
          <Row className="align-items-center m-5">
            
            <Col md={6} className="brdr" style={{borderLeft: "3px solid #d56b50"}}>
              <h1 className="subtitle">Our Vision</h1>
              <p className="subtext">Our vision is to become a global leader in education technology, shaping the future of learning by fostering curiosity, creativity, and critical thinking in learners around the world. By 2030, we aim to reach <b>1 million learners worldwide</b>, transforming educational landscapes and opening new opportunities for those who wish to explore, learn, and excel.</p>
            </Col>
            <Col md={6}>
              <Image src= {require("../../assets/about-2.jpeg")}  fluid />
            </Col>
          </Row>
        </Container>
      </div>
    );
};

export default About;
