import "./Courses.css";
import React, { useState } from "react";
import { Row, Col, Modal, Button } from "react-bootstrap";
import Card from "../../components/Card/Card.jsx";
import axios from "axios";
import { useEffect } from "react";

const Courses = () => {
    const [coursesData, setCoursesData] = useState([]);
    const [showModal, setShowModal] = useState(false);
    const [selectedCourse, setSelectedCourse] = useState(null);

    useEffect(() => {
        const fetchCourses = async () => {
            try {
                const response = await axios.get('http://localhost:5119/api/courses/with-instructors');
                setCoursesData(response.data);
                console.log(coursesData);
            } catch (error) {
                console.error("Failed to fetch courses", error);
            }
        };

        fetchCourses();
    }, []);

    const handleCardClick = (course) => {
        setSelectedCourse(course);
        setShowModal(true);
    };

    const handleCloseModal = () => {
        setShowModal(false);
        setSelectedCourse(null);
    };

    return (
        <div className="courses-main">
            <Row className="test">
                {coursesData.map((course, index) => (
                    <Col key={index} className="m-4">
                        <div onClick={() => handleCardClick(course)}>
                            <Card title={course.courseName} description={course.description} />
                        </div>
                    </Col>
                ))}
            </Row>

            {selectedCourse && (
                <Modal show={showModal} onHide={handleCloseModal} centered>
                    <Modal.Header closeButton>
                        <Modal.Title style={{ color: "white" }}>{selectedCourse.courseName}</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <p>{selectedCourse.Description}</p>
                        <p style={{color:"white"}}><i style={{ color: "#d56b50" }}>Instructor Name: {selectedCourse.instructorName}</i> </p>
                        <p><i style={{ color: "#d56b50" }}>Students Enrolled:</i>  </p>
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="primary">
                            Enroll
                        </Button>
                    </Modal.Footer>
                </Modal>
            )}
        </div>
    );
};

export default Courses;
