import { useState } from "react";
import { Modal, Button } from "react-bootstrap";
import "./profile.css";
import Card from "../../components/Card/Card";
import useAuth from "../../context&hook/useAuth.jsx";

const Profile = () => {
    const { user } = useAuth(); 
    const [showModal, setShowModal] = useState(false);
    const toggleShowModal = ()=>{
        setShowModal(!showModal);
    }
    const cardData = (
        <>
            {user.userEmail}
            <br />
            {user.userRole}
            <br />
            <br />
            <i>{"Click to see more details"}</i>
        </>
    );

    showExtraDetails = () => {
        console.log("laban");
    }

    return (    
        <div className="profile-main">   
        <img src={require("../../assets/user-logo.png")} alt="user-logo" id="user"/>
        <Card title = {user.userName} description={cardData} onclick = {showExtraDetails}></Card>
        </div>
    )
}



export default Profile;
